(function() {
  var activeClass = '.' + location.pathname.split("/")[3] + '-tab';
  $(activeClass).addClass('active');
}());

$('.carousel').each(function(){
  $(this).carousel({
    interval: false
  });
});

$("#answer-question").click(function(e) {
  e.preventDefault();
  $("#questions").animate({
    height: "350px",
    overflow: "auto"
  }, 500);
  $(this).hide();
  $("#next-question").show();
})

$("#finish").click(function(e) {
  e.preventDefault();
  window.location = $(this).attr('href')
})

